import{_ as e,o as a,c as r,Q as t}from"./chunks/framework.8adf7a9a.js";const f=JSON.parse('{"title":"Addons","titleTemplate":"Simple Voice Chat","description":"","frontmatter":{"titleTemplate":"Simple Voice Chat"},"headers":[],"relativePath":"minecraft/voicechat/addons.md","filePath":"minecraft/voicechat/addons.md"}'),o={name:"minecraft/voicechat/addons.md"},i=t('<h1 id="addons" tabindex="-1">Addons <a class="header-anchor" href="#addons" aria-label="Permalink to &quot;Addons&quot;">​</a></h1><p>Simple Voice Chat is designed to be easily extendable with an <a href="./api/overview">API</a>. This page lists all official addons for Simple Voice Chat.</p><h2 id="audioplayer" tabindex="-1">AudioPlayer <a class="header-anchor" href="#audioplayer" aria-label="Permalink to &quot;AudioPlayer&quot;">​</a></h2><p>This server side Fabric mod enables uploading custom audio for music discs and goat horns.</p><ul><li><a href="https://modrinth.com/mod/audioplayer" target="_blank" rel="noreferrer">Modrinth</a></li><li><a href="https://legacy.curseforge.com/minecraft/mc-mods/audioplayer" target="_blank" rel="noreferrer">CurseForge</a></li><li><a href="https://github.com/Navoei/CustomDiscs" target="_blank" rel="noreferrer">Third party Paper port</a></li></ul><h2 id="voice-chat-interaction" tabindex="-1">Voice Chat Interaction <a class="header-anchor" href="#voice-chat-interaction" aria-label="Permalink to &quot;Voice Chat Interaction&quot;">​</a></h2><p>This server side Fabric mod allows Simple Voice Chat to interact with your Minecraft world.</p><ul><li><a href="https://modrinth.com/mod/voice-chat-interaction" target="_blank" rel="noreferrer">Modrinth</a></li><li><a href="https://legacy.curseforge.com/minecraft/mc-mods/voice-chat-interaction" target="_blank" rel="noreferrer">CurseForge</a></li><li><a href="https://github.com/iGalaxyYT/voicechat-interaction-paper" target="_blank" rel="noreferrer">Third party Paper port</a></li></ul><h2 id="sound-physics-remastered" tabindex="-1">Sound Physics Remastered <a class="header-anchor" href="#sound-physics-remastered" aria-label="Permalink to &quot;Sound Physics Remastered&quot;">​</a></h2><p>A Minecraft mod that provides realistic sound attenuation, reverberation, and absorption through blocks. Optimized for the use with Simple Voice Chat.</p><ul><li><a href="https://modrinth.com/mod/sound-physics-remastered" target="_blank" rel="noreferrer">Modrinth</a></li><li><a href="https://legacy.curseforge.com/minecraft/mc-mods/sound-physics-remastered" target="_blank" rel="noreferrer">CurseForge</a></li></ul><h2 id="replay-voice-chat" tabindex="-1">Replay Voice Chat <a class="header-anchor" href="#replay-voice-chat" aria-label="Permalink to &quot;Replay Voice Chat&quot;">​</a></h2><p>A compatibility mod to record Simple Voice Chat with <a href="https://www.replaymod.com/" target="_blank" rel="noreferrer">ReplayMod</a>.</p><ul><li><a href="https://modrinth.com/mod/replay-voice-chat" target="_blank" rel="noreferrer">Modrinth</a></li><li><a href="https://legacy.curseforge.com/minecraft/mc-mods/replay-voice-chat" target="_blank" rel="noreferrer">CurseForge</a></li></ul><h2 id="simple-voice-chat-enhanced-groups" tabindex="-1">Simple Voice Chat Enhanced Groups <a class="header-anchor" href="#simple-voice-chat-enhanced-groups" aria-label="Permalink to &quot;Simple Voice Chat Enhanced Groups&quot;">​</a></h2><p>A server side Fabric mod providing useful features to Simple Voice Chat groups.</p><ul><li><a href="https://modrinth.com/mod/enhanced-groups" target="_blank" rel="noreferrer">Modrinth</a></li><li><a href="https://legacy.curseforge.com/minecraft/mc-mods/enhanced-groups" target="_blank" rel="noreferrer">CurseForge</a></li></ul><h2 id="simple-voice-chat-radio" tabindex="-1">Simple Voice Chat Radio <a class="header-anchor" href="#simple-voice-chat-radio" aria-label="Permalink to &quot;Simple Voice Chat Radio&quot;">​</a></h2><p>A server-side Simple Voice Chat addon mod for Fabric adding a radio block capable of streaming mp3 radio streams.</p><ul><li><a href="https://modrinth.com/mod/simple-voice-chat-radio" target="_blank" rel="noreferrer">Modrinth</a></li><li><a href="https://legacy.curseforge.com/minecraft/mc-mods/simple-voice-chat-radio" target="_blank" rel="noreferrer">CurseForge</a></li></ul><h2 id="wiretap" tabindex="-1">Wiretap <a class="header-anchor" href="#wiretap" aria-label="Permalink to &quot;Wiretap&quot;">​</a></h2><p>A server-side Simple Voice Chat addon mod for Fabric, that adds a microphone and a speaker block.</p><ul><li><a href="https://modrinth.com/mod/wiretap" target="_blank" rel="noreferrer">Modrinth</a></li><li><a href="https://legacy.curseforge.com/minecraft/mc-mods/wiretap" target="_blank" rel="noreferrer">CurseForge</a></li></ul><h2 id="voice-chat-broadcast" tabindex="-1">Voice Chat Broadcast <a class="header-anchor" href="#voice-chat-broadcast" aria-label="Permalink to &quot;Voice Chat Broadcast&quot;">​</a></h2><p>This is a simple Bukkit plugin that allows you to broadcast your voice with Simple Voice Chat.</p><ul><li><a href="https://github.com/henkelmax/voicechat-broadcast-plugin" target="_blank" rel="noreferrer">GitHub</a></li><li><a href="https://github.com/corfoto4/voicechat-broadcast-mod-fabric" target="_blank" rel="noreferrer">Third party Fabric port</a></li></ul>',26),l=[i];function c(h,s,n,d,p,m){return a(),r("div",null,l)}const b=e(o,[["render",c]]);export{f as __pageData,b as default};
