import{_ as e,o as i,c as a,Q as t}from"./chunks/framework.8adf7a9a.js";const u=JSON.parse('{"title":"Compatibility","titleTemplate":"Simple Voice Chat","description":"","frontmatter":{"titleTemplate":"Simple Voice Chat"},"headers":[],"relativePath":"minecraft/voicechat/wiki/compatibility.md","filePath":"minecraft/voicechat/wiki/compatibility.md"}'),o={name:"minecraft/voicechat/wiki/compatibility.md"},r=t('<h1 id="compatibility" tabindex="-1">Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;Compatibility&quot;">​</a></h1><h2 id="compatibility-between-mod-plugin-versions" tabindex="-1">Compatibility between mod/plugin versions <a class="header-anchor" href="#compatibility-between-mod-plugin-versions" aria-label="Permalink to &quot;Compatibility between mod/plugin versions&quot;">​</a></h2><p>Generally it is always recommended that you use the exact same version on the server and client for the best possible experience.</p><p>This mod follows <a href="https://semver.org/" target="_blank" rel="noreferrer">Semantic Versioning</a>. This means version <code>1.2.3</code> has a major version of <code>1</code>, a minor version of <code>2</code> and a patch version of <code>3</code>. So, as a rule of thumb, only the major and minor versions need to match. So for example you can join a server with version <code>1.2.3</code>, your client can also have version <code>1.2.0</code> or <code>1.2.5</code>.</p><h2 id="compatibility-between-minecraft-versions" tabindex="-1">Compatibility between Minecraft versions <a class="header-anchor" href="#compatibility-between-minecraft-versions" aria-label="Permalink to &quot;Compatibility between Minecraft versions&quot;">​</a></h2><p>Since version <code>2.1.0</code> of Simple Voice Chat, the mod is compatible between Minecraft versions with the help of tools like <a href="https://modrinth.com/plugin/viaversion" target="_blank" rel="noreferrer">ViaVersion</a>, <a href="https://modrinth.com/plugin/viabackwards" target="_blank" rel="noreferrer">ViaBackwards</a>, <a href="https://modrinth.com/mod/viafabric" target="_blank" rel="noreferrer">ViaFabric</a> or <a href="https://modrinth.com/mod/multiconnect" target="_blank" rel="noreferrer">multiconnect</a>. Note that you still have to consider the compatibility between the mod versions, as described above.</p><h2 id="compatibility-between-mod-plugin-loaders" tabindex="-1">Compatibility between mod/plugin loaders <a class="header-anchor" href="#compatibility-between-mod-plugin-loaders" aria-label="Permalink to &quot;Compatibility between mod/plugin loaders&quot;">​</a></h2><p>Simple Voice Chat is available for Fabric, Forge, Quilt and Bukkit/Spigot/Paper. All these versions are cross compatible with each other, considering the above limitations.</p><div class="danger custom-block"><p class="custom-block-title">NOTE</p><p>We don&#39;t support hybrid servers! Please either use only plugins or only mods on your server.</p></div><h2 id="operating-system-compatibility" tabindex="-1">Operating system compatibility <a class="header-anchor" href="#operating-system-compatibility" aria-label="Permalink to &quot;Operating system compatibility&quot;">​</a></h2><h3 id="windows" tabindex="-1">Windows <a class="header-anchor" href="#windows" aria-label="Permalink to &quot;Windows&quot;">​</a></h3><p>There are no known incompatibilities with any modern version of Windows.</p><h3 id="macos" tabindex="-1">MacOS <a class="header-anchor" href="#macos" aria-label="Permalink to &quot;MacOS&quot;">​</a></h3><p>MacOS version <code>10.15</code> or later is required to run Simple Voice Chat on a Mac. We support both Intel and ARM based Macs.</p><h3 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux&quot;">​</a></h3><p>Simple Voice Chat should work with every major Linux distribution, as long as it is up-to-date. If you encounter any incompatibility on Linux, please let us know.</p>',16),n=[r];function s(l,c,m,d,h,p){return i(),a("div",null,n)}const f=e(o,[["render",s]]);export{u as __pageData,f as default};
