import{_ as e,o as t,c as d,Q as o}from"./chunks/framework.8adf7a9a.js";const _=JSON.parse('{"title":"Configuration","titleTemplate":"Simple Voice Chat","description":"","frontmatter":{"titleTemplate":"Simple Voice Chat","aside":false},"headers":[],"relativePath":"minecraft/voicechat/wiki/configuration.md","filePath":"minecraft/voicechat/wiki/configuration.md"}'),c={name:"minecraft/voicechat/wiki/configuration.md"},r=o('<h1 id="configuration" tabindex="-1">Configuration <a class="header-anchor" href="#configuration" aria-label="Permalink to &quot;Configuration&quot;">​</a></h1><div class="danger custom-block"><p class="custom-block-title">NOTE</p><p>If you are using older versions of the mod (older than 2.4.0), please check out <a href="./old_configuration">this</a>.</p></div><h2 id="troubleshooting-config-issues" tabindex="-1">Troubleshooting Config Issues <a class="header-anchor" href="#troubleshooting-config-issues" aria-label="Permalink to &quot;Troubleshooting Config Issues&quot;">​</a></h2><p>While editing configuration files, make sure the client/server is stopped. If the config values keep resetting, this is most likely the problem.</p><p>If you can&#39;t find the config files, make sure the client/server was started at least once, so that the files are generated.</p><h2 id="server" tabindex="-1">Server <a class="header-anchor" href="#server" aria-label="Permalink to &quot;Server&quot;">​</a></h2><p><strong>Fabric/Forge/Quilt</strong></p><p><code>config/voicechat/voicechat-server.properties</code></p><p><strong>Bukkit/Spigot/Paper</strong></p><p><code>plugins/voicechat/voicechat-server.properties</code></p><table><thead><tr><th>Config key</th><th>Description</th><th>Default value</th></tr></thead><tbody><tr><td><code>port</code></td><td>The port of the voice chat server<br>Setting this to <code>-1</code> sets the port to the Minecraft servers port (Not recommended)</td><td><code>24454</code></td></tr><tr><td><code>bind_address</code></td><td>The IP address to bind the voice chat server on<br>Leave empty to use <code>server-ip</code> of server.properties<br>To bind to the wildcard address, use <code>*</code></td><td><code></code></td></tr><tr><td><code>max_voice_distance</code></td><td>The distance to where the voice can be heard</td><td><code>48</code></td></tr><tr><td><code>crouch_distance_multiplier</code></td><td>The multiplier of the voice distance when crouching</td><td><code>1.0</code></td></tr><tr><td><code>whisper_distance_multiplier</code></td><td>The multiplier of the voice distance when whispering</td><td><code>0.5</code></td></tr><tr><td><code>codec</code></td><td>The opus codec<br>Possible values are <code>VOIP</code>, <code>AUDIO</code> and <code>RESTRICTED_LOWDELAY</code></td><td><code>VOIP</code></td></tr><tr><td><code>mtu_size</code></td><td>The maximum size in bytes that voice packets are allowed to have</td><td><code>1024</code></td></tr><tr><td><code>keep_alive</code></td><td>The frequency at which keep alive packets are sent<br>Setting this to a higher value may result in timeouts</td><td><code>1000</code></td></tr><tr><td><code>enable_groups</code></td><td>If group chats are allowed</td><td><code>true</code></td></tr><tr><td><code>voice_host</code></td><td>The host name that clients should use to connect to the voice chat<br>This may also include a port, e.g. &#39;example.com:24454&#39;<br>Don&#39;t change this value if you don&#39;t know what you are doing</td><td><code></code></td></tr><tr><td><code>allow_recording</code></td><td>If players are allowed to record the voice chat</td><td><code>true</code></td></tr><tr><td><code>spectator_interaction</code></td><td>If spectators are allowed to talk to other players</td><td><code>false</code></td></tr><tr><td><code>spectator_player_possession</code></td><td>If spectators can talk to players they are spectating</td><td><code>false</code></td></tr><tr><td><code>force_voice_chat</code></td><td>If players without the mod should get kicked from the server</td><td><code>false</code></td></tr><tr><td><code>login_timeout</code></td><td>The amount of milliseconds, the server should wait to check if the player has the mod installed<br>Only active when force_voice_chat is set to true</td><td><code>10 000</code></td></tr><tr><td><code>broadcast_range</code></td><td>The range where the voice chat should broadcast audio to<br>A value &lt;0 means <code>max_voice_distance</code></td><td><code>-1.0</code></td></tr></tbody></table><h2 id="client" tabindex="-1">Client <a class="header-anchor" href="#client" aria-label="Permalink to &quot;Client&quot;">​</a></h2><p><strong>Fabric/Forge/Quilt</strong></p><p><code>config/voicechat/voicechat-client.properties</code></p><table><thead><tr><th>Config key</th><th>Description</th><th>Default value</th></tr></thead><tbody><tr><td><code>voice_chat_volume</code></td><td>The voice chat volume</td><td><code>1.0</code></td></tr><tr><td><code>voice_activation_threshold</code></td><td>The threshold for voice activation in dB</td><td><code>-50.0</code></td></tr><tr><td><code>microphone_amplification</code></td><td>The voice chat microphone amplification</td><td><code>1.0</code></td></tr><tr><td><code>microphone_activation_type</code></td><td>Microphone activation type<br>Possible values are <code>PTT</code> and <code>VOICE</code></td><td><code>PTT</code></td></tr><tr><td><code>output_buffer_size</code></td><td>The size of the audio output buffer in packets<br>Higher values mean a higher latency, but less crackles<br>Increase this value if you have an unstable internet connection</td><td><code>5</code></td></tr><tr><td><code>audio_packet_threshold</code></td><td>The maximum amount of audio packets that are held back if a packet arrives out of order or gets dropped<br>This prevents discarding audio packets that are slightly out of order<br>Set this to <code>0</code> to disable</td><td><code>3</code></td></tr><tr><td><code>voice_deactivation_delay</code></td><td>The time it takes for the microphone to deactivate when using voice activation<br>A value of 1 means 20 milliseconds, 2=40 ms, 3=60 ms, ...</td><td><code>25</code></td></tr><tr><td><code>microphone</code></td><td>The microphone used by the voice chat<br>Empty for default device</td><td><code></code></td></tr><tr><td><code>speaker</code></td><td>The speaker used by the voice chat<br>Empty for default device</td><td><code></code></td></tr><tr><td><code>muted</code></td><td>If the microphone is muted (only when using voice activation)</td><td><code>false</code></td></tr><tr><td><code>disabled</code></td><td>If the voice chat is disabled (sound and microphone off)</td><td><code>false</code></td></tr><tr><td><code>hide_icons</code></td><td>If the voice chat icons should be hidden</td><td><code>false</code></td></tr><tr><td><code>show_group_hud</code></td><td>If the group HUD should be visible</td><td><code>true</code></td></tr><tr><td><code>show_own_group_icon</code></td><td>If the own icon should be shown when in a group</td><td><code>true</code></td></tr><tr><td><code>group_hud_icon_scale</code></td><td>The scale of the group HUD</td><td><code>2.0</code></td></tr><tr><td><code>group_player_icon_orientation</code></td><td>The orientation of the player icons in the group HUD<br>Possible values are <code>VERTICAL</code> and <code>HORIZONTAL</code></td><td><code>VERTICAL</code></td></tr><tr><td><code>group_player_icon_pos_x</code></td><td>The X position of the player icons in the group HUD<br>Negative values mean anchoring to the right</td><td><code>4</code></td></tr><tr><td><code>group_player_icon_pos_y</code></td><td>The Y position of the player icons in the group HUD<br>Negative values mean anchoring to the bottom</td><td><code>4</code></td></tr><tr><td><code>hud_icon_pos_x</code></td><td>The X position of the HUD icons<br>Negative values mean anchoring to the right</td><td><code>16</code></td></tr><tr><td><code>hud_icon_pos_y</code></td><td>The Y position of the HUD icons<br>Negative values mean anchoring to the bottom</td><td><code>-16</code></td></tr><tr><td><code>hud_icon_scale</code></td><td>The scale of the HUD icons</td><td><code>1.0</code></td></tr><tr><td><code>recording_destination</code></td><td>The location where recordings should be saved<br>Leave empty for default location</td><td><code></code></td></tr><tr><td><code>recording_quality</code></td><td>The quality of the recorded audio<br>0 = highest quality, 9 = lowest quality</td><td><code>2</code></td></tr><tr><td><code>denoiser</code></td><td>If noise cancellation should be enabled</td><td><code>false</code></td></tr><tr><td><code>run_local_server</code></td><td>If voice chat should work in singleplayer/LAN worlds</td><td><code>true</code></td></tr><tr><td><code>java_microphone_implementation</code></td><td>Whether to use the Java implementation of microphone capturing instead of OpenAL</td><td><code>true</code> for MacOS, <code>false</code> for every other OS</td></tr><tr><td><code>macos_check_microphone_permission</code></td><td>If the mod should check for microphone permissions (MacOS only)</td><td><code>true</code></td></tr><tr><td><code>show_fake_players_disconnected</code></td><td>If fake players should have the disconnected icon above their head</td><td><code>false</code></td></tr><tr><td><code>offline_player_volume_adjustment</code></td><td>If the volume adjustment GUI should also show offline players</td><td><code>false</code></td></tr><tr><td><code>audio_type</code></td><td>The 3D audio type<br>Possible values are <code>NORMAL</code>, <code>REDUCED</code> and <code>OFF</code></td><td><code>NORMAL</code></td></tr><tr><td><code>use_natives</code></td><td>If the mod should load native libraries<br>If set to false, the Java Opus implementation will be used, the denoiser won&#39;t be available and you won&#39;t be able to record audio.</td><td><code>true</code></td></tr><tr><td><code>freecam_support</code></td><td>This lets you hear players near you, even though you are further away with your freecam<br>See <a href="freecam_support">this</a> for more information</td><td><code>true</code></td></tr><tr><td><code>mute_on_join</code></td><td>If enabled, you will be automatically muted when joining a world</td><td><code>false</code></td></tr></tbody></table>',15),a=[r];function i(s,n,h,l,u,p){return t(),d("div",null,a)}const m=e(c,[["render",i]]);export{_ as __pageData,m as default};
